<?phpclass ClientContactsCommand extends Command {	public function __construct() {		parent::__construct(AccessMode::USER);	}		public function Execute () {		$contactsTemplate = new Template('ClientContacts');				$user = new User();		if (!$user->RestoreFromSession()) {			Command::Redirect('ClientCart');		}		$contactsTemplate->SetParam('userInfo', $user->GetInfo());		wrapClientPanelTemplate('Моя контактная информация', $contactsTemplate);		return true;	}}